module.exports.BadRequestException = function (message) {
    return { statusCode: 400, message: message };
};

module.exports.DataNotFoundException = function (id) {
    return { statusCode: 404, message: id };
};

module.exports.MandatoryFieldException = function (fieldName) {
    return { statusCode: 400, message: "Mandatory field missing in the request (" + fieldName + ")" };
};

module.exports.InvalidDataException = function (fieldName) {
    return { statusCode: 405, message: "Invalid data for the field (" + fieldName + ")." };
};

module.exports.InvalidEnumException = function (fieldName, possibleEnum) {
    return { statusCode: 405, message: "Invalid data for the field (" + fieldName + "). Possible values are [" + possibleEnum + "]" };
}; 

module.exports.ForbiddenException = function (message) {
    return { statusCode: 403, message: message };
}